mapper.select:
SELECT *
FROM products p
WHERE id = #{id}

mapper.update:
UPDATE products
   SET name = #{name}
      ,trainer_no = #{trainerNo}
      ,content = #{content}
      ,price = #{price}
      ,updated_at = now()
WHERE id = #{id}

UPDATE products
   SET name = #{name}
      ,description = #{description}
      ,content = #{content}
      ,updated_at = now()
WHERE id = #{id}
